import React, {useEffect, useState} from 'react';
import {PersonalTaskRepository} from "@entities/task";
import {ITask} from "@entities/task";
import {ITaskStatus} from "@entities/status";
import {ITaskTag} from "@entities/tag";
import {LoaderHandler} from "@shared/ui";

export const TaskTable = () => {
    const [tasks, setTasks] = useState<ITask<ITaskStatus, ITaskTag>[] | null>(null);

    useEffect(() => {
        const getTasks = async () => {
            const response = await PersonalTaskRepository.getList();
            await new Promise(resolve => setTimeout(resolve, 2000));
            setTasks(response.results);
        };

        getTasks();
    }, []);

    return (
        <div>
            <div>
                
            </div>

            <LoaderHandler isLoaded={!!tasks}>
                {
                    tasks?.map(task => (
                        <p key={task.id}>{task.name}</p>
                    ))
                }
            </LoaderHandler>
        </div>
    );
};
